INSERT INTO `wp_posts` VALUES (49,1,'2022-05-08 09:04:53','2022-05-08 09:04:53','<h3>Notre équipe</h3>		\n			Composer de producteurs et de passionnés de musique, chaque personne chez Soundwide est là pour vous !		\n								<figure><img src=\"http://localhost/wp-content/uploads/2022/05/Ka-150x150.jpg\" alt=\"Ka Jim\" /><figcaption>Ka Jim</figcaption></figure><figure><img src=\"http://localhost/wp-content/uploads/2022/05/david-150x150.jpg\" alt=\"Laurent Da\" /><figcaption>Laurent Da</figcaption></figure><figure><img src=\"http://localhost/wp-content/uploads/2022/05/tim-150x150.jpg\" alt=\"Tim Baland\" /><figcaption>Tim Baland</figcaption></figure>			\n												Previous\n												Next\n			<h2>Soundwide permet de trouver sa vibe !</h2>		\n			<iframe frameborder=\"0\" scrolling=\"no\" marginheight=\"0\" marginwidth=\"0\"\n					src=\"https://maps.google.com/maps?q=Paris%2C%2012%20rue%20Louis&#038;t=m&#038;z=16&#038;output=embed&#038;iwloc=near\"\n					title=\"Paris, 12 rue Louis\"\n					aria-label=\"Paris, 12 rue Louis\"\n			></iframe>\n		<section style=\"box-sizing: inherit; color: #fff; font-family: \'Libre Franklin\', \'Helvetica Neue\', helvetica, arial, sans-serif; font-size: 14px;\">\n<p style=\"box-sizing: inherit;\">Soundwide propose des instruments MIDI pour les producteurs de musique.</p>\n<p style=\"box-sizing: inherit;\">Contactez-nous pour plus de renseignements!</p>\n</section>		\n			<a href=\"#\" role=\"button\">\n						Nous contacter\n					</a>','Qui sommes nous ?','','publish','closed','closed','','qui-sommes-nous','','','2022-05-09 00:23:07','2022-05-09 00:23:07','',0,'http://localhost/?page_id=49',0,'page','',0);
